/**
 * 如果全部单个文件丢在node_modules上，它会显得杂乱无章，所以我们应该顶一个自己的模块：jsliang-module，然后将我们的tools.js存放在该目录中
 */

var tools = {
  add: (...numbers) => {
    let sum = 0
    for (let number in numbers) {
      sum += numbers[number]
    }
    return sum
  },
  multiply: (...numbers) => {
    let sum = numbers[0]
    for (let number in numbers) {
      sum = sum * numbers[number]
    }
    return sum
  }
}

module.exports = tools
